    private void dataTypeFactorialButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_dataTypeFactorialButtonActionPerformed
        // TODO add your handling code here:
        String f = numTextField.getText();
        long fact = Long.parseLong(f);
        long fac = 1;
        for (int i = 1; i <= fact; i++) {
            fac = fac * i;
        }
        String factorial = "" + fac;
        textArea.setText(factorial);

    }//GEN-LAST:event_dataTypeFactorialButtonActionPerformed

