    private void bugnumberMultiplyButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bugnumberMultiplyButtonActionPerformed
        // TODO add your handling code here:
        String s1=number1TextField.getText();
        String s2=number2TextField.getText();
        BigInteger num1=new BigInteger(s1);
        BigInteger num2=new BigInteger(s2);
        BigInteger Multiplication;
        Multiplication=num1.multiply(num2);
        String s3=Multiplication.toString();
        textArea.setText(s3);
    }//GEN-LAST:event_bugnumberMultiplyButtonActionPerformed

