    private void bignumberFactorialButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_bignumberFactorialButtonActionPerformed
        // TODO add your handling code here:
        String s=numberTextField.getText();
        long n=Long.parseLong(s);
        BigInteger fact=new BigInteger("1");
        for (long i = 1; i <=n; i++)
        {
            fact=fact.multiply(BigInteger.valueOf(i));
        }
        String s1=fact.toString();
        
        boolean stat = false;
        String ans="";
        for(int i=0;i<s1.length();i++)
        {
            ans+=s1.charAt(i);
            if(i%180==0 && stat)
                ans+='\n';
            stat=true;
        }
        textArea.setText(ans);
    }//GEN-LAST:event_bignumberFactorialButtonActionPerformed

